import { Axios } from "axios";
const getItem = () =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
      setItemList(response.data);
    });
  };

const InventoryInfo = ({inventory}) => {
    return(
        <table >
            <thead>
            <tr>
                <td >Category</td>
                <td >Name</td>
                <td >Description</td>
                <td >Stock</td>
            </tr>
            </thead>
            <tbody>
            {inventory.map((items) =>(
                <tr  key={items.id}>
                    <td>{items.name}</td>
                    <td >{items.description}</td>
                    <td>{items.location}</td>
                    <td>{items.stock}</td>
                </tr>
            ))}
            </tbody>
        </table>
    )
}

export default InventoryInfo;
