import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from "react";
import Axios from 'axios';
function App() {

  //What the database will store
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [stock, setStock] = useState(0);
  const [location, setLocation] = useState("");

  const [itemList, setItemList] = useState([]);

  //adds item to database
  const addItem = () =>{
    Axios.post('http://localhost:3001/create', {
      name: name,
      desc: desc,
      stock: stock,
      location: location
    }).then(() =>{
        setItemList([...itemList, {
          name: name,
          description: desc,
          stock: stock,
          location: location,
        },]);
    });
  };


  useEffect(() =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
      setItemList(response.data);
    });
  },[]);

  const deleteItem = (id) =>{
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) =>{
        setItemList(itemList.filter((val) =>{
            return val.id !== id
        }))
    });
}

  return (
    <div className="App">
      <form className='text-center'>
        <div className='row mb-3'>
          <label>Name:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Description:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setDesc(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Location:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setLocation(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Stock:</label>
          <div className='col-sm'>
          <input type="number" onChange={(event)=>{setStock(event.target.value)}}/>
          </div>
        </div>
          <button onClick={addItem} className="btn btn-dark mb-2">Add Item</button>
      </form>
      <div className="items">
        <table className="table table-bordered">
          <thead className="table-dark">
          <tr>
            <th scope='col'>Name</th>
            <th scope='col'>Description</th>
            <th scope='col'>Location</th>
            <th scope='col'>In-Stock</th>
            <th className='col-2'></th>
          </tr>
          </thead>
          <tbody>
        {itemList.map((val, key) =>(
          <tr>
            <td>{val.name}</td>
            <td>{val.description}</td>
            <td>{val.location}</td>
            <td>{val.stock}</td>
            <td><button onClick={()=>{deleteItem(val.id)}} className="btn btn-dark">Delete</button><button className='btn btn-dark'>Update</button></td>
          </tr>
        ))}
          </tbody>
        </table>

      </div>
    </div>
  );
}

export default App;
