/*
  Add alert for delete button
*/

import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from "react";
import Axios from 'axios';
function App() {

  //What the database will store
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [stock, setStock] = useState(0);
  const [location, setLocation] = useState("");

  const [newName, setNewName] = useState({name: name});
  const [newDesc, setNewDesc] = useState("");
  const [newStock, setNewStock] = useState(0);
  const [newLocation, setNewLocation] = useState("");

  const [itemList, setItemList] = useState([]);

  //adds item to database
  //0, negative numbers
  const addItem = () =>{
    Axios.post('http://localhost:3001/create', {
      name: name,
      desc: desc,
      stock: stock,
      location: location
    }).then(() =>{
        setItemList([...itemList, {
          name: name,
          description: desc,
          stock: stock,
          location: location,
        },]);
    });
  };


  useEffect(() =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
      setItemList(response.data);
    });
  },[]);

  const deleteItem = (id) =>{
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) =>{
        setItemList(itemList.filter((val) =>{
            return val.id !== id
        }))
    });
}

const updateItem = (id) =>{
    Axios.put('http://localhost:3001/update', {name: newName, desc: newDesc, stock: newStock, location: newLocation, id: id}).then((response) =>{
      setItemList(itemList.map((val) =>{
        return val.id === id ? {id: val.id, name: newName, desc: newDesc, stock: newStock, location: newLocation} : 
        val;
      }))
    });
}

const cnfmDelete = (id) =>{
  if(window.confirm("Are you sure you want to delete this item?")){
    deleteItem(id);
  }
}

  return (
    <div className="App">
      <form className='text-center'>
        <div className='row mb-3'>
          <label>Name:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Description:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setDesc(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Location:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setLocation(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Stock:</label>
          <div className='col-sm'>
          <input type="number" min="0" onChange={(event)=>{setStock(event.target.value)}}/>
          </div>
        </div>
          <button onClick={addItem} className="btn btn-dark mb-2">Add Item</button>
      </form>
      <div className="items table-responsive">
        <table className="table table-bordered">
          <thead className="table-dark">
          <tr>
            <th scope='col' className='col-sm-2'>Name</th>
            <th scope='col' className='col-sm-2'>Description</th>
            <th scope='col' className='col-sm-2'>Location</th>
            <th scope='col' className='col-sm-2'>In-Stock</th>
            <th scope='col' className='col-sm-2'></th>
          </tr>
          </thead>
          <tbody>
        {itemList.map((val, key) =>(
          <tr>
            <td><input type="text" value={val.name} onChange={(event) => {setNewName(event.target.value)}}/></td>
            <td><input type="text" value={val.description} onChange={(event) => {setNewDesc(event.target.value)}}/></td>
            <td><input type="text" value={val.location} onChange={(event) => {setNewLocation(event.target.value)}}/></td>
            <td><input type="number" min="0" value={val.stock} onChange={(event) => {setNewStock(event.target.value)}}/></td>
            <td><button onClick={()=>{cnfmDelete(val.id)}} className="btn btn-dark text-nowrap">Delete</button><button onClick={()=>{updateItem(val.id)}} className='btn btn-dark text-nowrap'>Update</button></td>
          </tr>
        ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
