import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";
import Axios from 'axios';
import InventoryInfo from './inventoryinfo';
function App() {

  //What the database will store
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [stock, setStock] = useState(0);
  const [location, setLocation] = useState("");

  const [itemList, setItemList] = useState([]);

  //adds item to database
  const addItem = () =>{
    Axios.post('http://localhost:3001/create', {
      name: name,
      desc: desc,
      stock: stock,
      location: location
    }).then(() =>{
        setItemList([...itemList, {
          name: name,
          description: desc,
          stock: stock,
          location: location,
        },]);
    });
  };

  const getItem = () =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
      setItemList(response.data);
    });
  };

  return (
    <div className="App">
      <form className='text-center'>
        <div className='row mb-3'>
          <label>Name:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Description:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setDesc(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Location:</label>
          <div className='col-sm'>
          <input type="text" onChange={(event)=>{setLocation(event.target.value)}}/>
          </div>
        </div>
        <div className='row mb-3'>
          <label>Stock:</label>
          <div className='col-sm'>
          <input type="number" onChange={(event)=>{setStock(event.target.value)}}/>
          </div>
        </div>
          <button onClick={addItem} className="btn btn-dark mb-2">Add Item</button>
      </form>
      <button onClick={getItem} className="btn btn-dark mb-2">Show Items</button>
      <div className="items">
                <table className="table">
                  <thead className="table-dark">
                  <tr>
                    <th scope='col'>Name</th>
                    <th scope='col'>Description</th>
                    <th scope='col'>Location</th>
                    <th scope='col'>In-Stock</th>
                  </tr>
                  </thead>
                  <tbody>
                {itemList.map((val, key) =>(
                  <tr key={key}>
                    <td>{val.name}</td>
                    <td>{val.description}</td>
                    <td>{val.location}</td>
                    <td>{val.stock}</td>
                  </tr>
                ))}
                  </tbody>
                </table>
      </div>
    </div>
  );
}

export default App;
