import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";
import Axios from 'axios';

function App() {

  //What the database will store
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [stock, setStock] = useState(0);
  const [location, setLocation] = useState("");

  const [itemList, setItemList] = useState([]);

  //adds item to database
  const addItem = () =>{
    Axios.post('http://localhost:3001/create', {
      name: name,
      desc: desc,
      stock: stock,
      location: location
    }).then(() =>{
        setItemList([...itemList, {
          name: name,
          description: desc,
          stock: stock,
          location: location,
        },]);
    });
  };

  const getItem = () =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
      setItemList(response.data);
    });
  };

  return (
    <div className="App">
      <div className="information">
          <label>Name:</label>
          <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
          <label>Description:</label>
          <input type="text" onChange={(event)=>{setDesc(event.target.value)}}/>
          <label>Location:</label>
          <input type="text" onChange={(event)=>{setLocation(event.target.value)}}/>
          <label>Stock:</label>
          <input type="number" onChange={(event)=>{setStock(event.target.value)}}/>
          <button onClick={addItem}>Add Item</button>
      </div>
      <div className="items">
          <button onClick={getItem}>Show Items</button>
          {itemList.map((val, key) =>{
            return(
                <table className="table table-dark">
                  <thead className='thead-dark'>
                  <tr>
                    <th scope='col'>Name</th>
                    <th scope='col'>Description</th>
                    <th scope='col'>Location</th>
                    <th scope='col'>In-Stock</th>
                  </tr>
                  </thead>
                  <tbody>
                  <tr key={key}>
                    <td>{val.name}</td>
                    <td>{val.description}</td>
                    <td>{val.location}</td>
                    <td>{val.stock}</td>
                  </tr>
                  </tbody>
                </table>
            )
          })}
      </div>
    </div>
  );
}

export default App;
