import React from "react";
import { useState, useCallback } from "react";

const Edit = ({item, onDelete, onUpdate, onHandleChange}) =>{
// Hook
// Parameter is the boolean, with default "false" value
  const useToggle = (initialState = false) => {
    // Initialize the state
    const [state, setState] = useState(initialState);

    // Define and memorize toggler function in case we pass down the component,
    // This function change the boolean value to it's opposite value
    const toggle = useCallback(() => setState(state => !state), []);

    return [state, toggle]
  }

  const [edit, setEdit] = useToggle();

  return(
    !edit ?
          <tr key={item.id} style={item.stock === 0 || item.stock <= 5 ? {borderColor:"red"}: {borderColor: "black"}} >
            <td>{item.name}</td>
            <td>{item.description}</td>
            <td>{item.location}</td>
            <td>{item.stock}</td>
            <td><button onClick={()=>{onDelete(item.id)}} className="btn btn-danger text-nowrap">Delete</button><button onClick={()=>{setEdit()}} className='btn btn-dark text-nowrap'>Edit</button></td>
          </tr> :
          <tr key={item.id} style={item.stock === 0 || item.stock <= 5 ? {borderColor:"red"}: {borderColor: "black"}} > 
          <td><input type="text" defaultitemue={item.name} onChange={onHandleChange}/></td>
          <td>{item.description}</td>
          <td>{item.location}</td>
          <td>{item.stock}</td>
          <td><button onClick={()=>{onUpdate(item.id)}}>Update</button><button onClick={()=>{onDelete(item.id)}} className="btn btn-danger text-nowrap">Delete</button><button  onClick={()=>{setEdit()}} className='btn btn-dark text-nowrap'>Edit</button></td>
        </tr>
  )
}

export default Edit;
