import Axios from "axios";

const InventoryInfo = () =>{
    const getItem = () =>{
        Axios.get('http://localhost:3001/inventory').then((response) =>{
        return(
            <div>
            <table >
                <thead>
                <tr>
                    <td>Category</td>
                    <td>Name</td>
                    <td>Description</td>
                    <td>Stock</td>
                </tr>
                </thead>
                <tbody>
                {response.data.map((items) =>(
                    <tr  key={items.id}>
                        <td>{items.name}</td>
                        <td >{items.description}</td>
                        <td>{items.location}</td>
                        <td>{items.stock}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            </div>
        )
    });

    }
    return (
        <div>
            {getItem}
        </div>
    )

  };

export default InventoryInfo;
