import Axios from "axios";
const InventoryInfo = ({inventory}) =>{
    Axios.get('http://localhost:3001/inventory').then((response) =>{
        return(
            <div>
            <table>
                <thead>
                <tr>
                    <td>Category</td>
                    <td>Name</td>
                    <td>Description</td>
                    <td>Stock</td>
                </tr>
                </thead>
                <tbody>
                {inventory.data.map((item) =>(
                  <tr>
                    <td>{item.name}</td>
                    <td>{item.description}</td>
                    <td>{item.location}</td>
                    <td>{item.stock}</td>
                  </tr>
                ))}
                </tbody>
            </table>
            </div>
        )});
  };

export default InventoryInfo;
