import './App.css';
import { useState } from "react";
import { Axios } from 'axios';

function App() {

  //What the database will store
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");
  const [stock, setStock] = useState(0);
  const [location, setLocation] = useState("");

  const [itemList, setItemList] = useState([]);

  //adds item to database
  const addItem = () =>{
    Axios.post('http://localhost:3001/create', {
      name: name,
      desc: desc,
      stock: stock,
      location: location
    }).then(() =>{
        setItemList([...itemList, {
          name: name,
          desc: desc,
          stock: stock,
          location: location,
        },]);
    });
  };

  return (
    <div className="App">
      <div className="information">
          <label>Name:</label>
          <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
          <label>Description:</label>
          <input type="text" onChange={(event)=>{setDesc(event.target.value)}}/>
          <label>Location:</label>
          <input type="number" onChange={(event)=>{setLocation(event.target.value)}}/>
          <label>Stock:</label>
          <input type="number" onChange={(event)=>{setStock(event.target.value)}}/>
          <button onClick={addItem}>Add Item</button>
      </div>
    </div>
  );
}

export default App;
